#!/bin/python3

SIGNAL = ""
SIZE_OF_SINGAL = len(SIGNAL)
SIZE_OF_PACKET_START_SEQUENCE = 4
SIZE_OF_MESSAGE = 14

for letter_index in range(SIZE_OF_SINGAL - SIZE_OF_PACKET_START_SEQUENCE - 1):
    potential_start_sequence = SIGNAL[
        letter_index : letter_index + SIZE_OF_PACKET_START_SEQUENCE
    ]
    if len(set(potential_start_sequence)) == SIZE_OF_PACKET_START_SEQUENCE:
        print(f"The START_SEQUENCE: {letter_index+SIZE_OF_PACKET_START_SEQUENCE}")
        break

for letter_index in range(SIZE_OF_SINGAL - SIZE_OF_PACKET_START_SEQUENCE - 1):
    potential_start_sequence = SIGNAL[letter_index : letter_index + SIZE_OF_MESSAGE]
    if len(set(potential_start_sequence)) == SIZE_OF_MESSAGE:
        print(f"The MESSAGE_SEQUENCE: {letter_index+SIZE_OF_MESSAGE}")
        break
